        if (value instanceof Currency) {
          throw new Error('currency must be a valid OBJECT');
        }
        this._currency = value;
      }

      // Method to display full currency
      displayFullPrice() {
        return `${this._amount} ${this._currency.name} (${this._currency.code})`;
      }

      static convertPrice(number, conversionRate) {
        if (typeof number !== 'number' || typeof conversionRate !== 'number')
            throw new Error("Number or ConversionRate must be a valid number")
        return number * conversionRate;
